mapper.select:
SELECT *
FROM (
         SELECT ROWNUM R, TQ.*
         FROM TB_QNA_HBS TQ
         WHERE TQ.DELETE_YN = 'N'
           AND TITLE LIKE '%' || #{title} || '%'
           AND ROWNUM < = (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

SELECT *
FROM (
         SELECT ROWNUM R, TQ.*
         FROM TB_QNA_HBS TQ
         WHERE TQ.DELETE_YN = 'N'
         AND ROWNUM < = (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}
